import type { Meta, StoryObj } from "@storybook/react";
import { userEvent, within } from "@storybook/test";

import { Button, type ButtonProps } from "./button";

const meta: Meta<ButtonProps> = {
  title: "UI/Button",
  component: Button,
  argTypes: {
    variant: {
      control: "select",
      options: ["default", "destructive", "outline", "secondary", "ghost", "link"],
    },
    size: {
      control: "select",
      options: ["default", "sm", "lg", "icon"],
    },
  },
  parameters: {
    backgrounds: {
      default: "light",
      values: [
        { name: "light", value: "var(--background)" },
        { name: "dark", value: "hsl(var(--foreground))" },
      ],
    },
  },
};

export default meta;

type Story = StoryObj<ButtonProps>;

export const Default: Story = {
  args: { children: "Button" },
};

export const Variants: Story = {
  render: (args) => (
    <div className="flex flex-wrap gap-3">
      <Button {...args} variant="default">
        Default
      </Button>
      <Button {...args} variant="destructive">
        Destructive
      </Button>
      <Button {...args} variant="outline">
        Outline
      </Button>
      <Button {...args} variant="secondary">
        Secondary
      </Button>
      <Button {...args} variant="ghost">
        Ghost
      </Button>
      <Button {...args} variant="link">
        Link
      </Button>
    </div>
  ),
  args: { children: "Button" },
};

export const Sizes: Story = {
  render: (args) => (
    <div className="flex flex-wrap items-center gap-3">
      <Button {...args} size="sm">
        Small
      </Button>
      <Button {...args} size="default">
        Default
      </Button>
      <Button {...args} size="lg">
        Large
      </Button>
      <Button {...args} size="icon" aria-label="Icon button">
        ⭐️
      </Button>
    </div>
  ),
  args: { children: "Button" },
};

export const Interactions: Story = {
  args: { children: "Click me" },
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement);
    const button = await canvas.findByRole("button", { name: /click me/i });
    await userEvent.click(button);
  },
};

import * as React from "react";
import { cn } from "@/lib/utils";

export interface ButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: "default" | "destructive" | "outline" | "secondary" | "ghost" | "link";
  size?: "default" | "sm" | "lg" | "icon";
}

const variantStyles = {
  default: "bg-primary text-primary-foreground hover:bg-primary/90",
  destructive: "bg-destructive text-destructive-foreground hover:bg-destructive/90",
  outline: "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
  secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
  ghost: "hover:bg-accent hover:text-accent-foreground",
  link: "text-primary underline-offset-4 hover:underline",
};

const sizeStyles = {
  default: "h-10 px-4 py-2",
  sm: "h-9 rounded-md px-3",
  lg: "h-11 rounded-md px-8",
  icon: "h-10 w-10",
};

export const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant = "default", size = "default", ...props }, ref) => {
    return (
      <button
        className={cn(
          "inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
          variantStyles[variant],
          sizeStyles[size],
          className,
        )}
        ref={ref}
        {...props}
      />
    );
  },
);

Button.displayName = "Button";

import type { Meta, StoryObj } from "@storybook/react";
import { userEvent, within } from "@storybook/test";

import { CopyButton, type CopyButtonProps } from "./copy-button";

const meta: Meta<CopyButtonProps> = {
  title: "UI/CopyButton",
  component: CopyButton,
};

export default meta;

type Story = StoryObj<CopyButtonProps>;

export const Default: Story = {
  args: {
    value: "pnpm dlx shadcn@latest add https://example.com/registry/button.json",
    children: "复制指令",
  },
  play: async ({ canvasElement }) => {
    const canvas = within(canvasElement);
    const button = await canvas.findByRole("button", { name: /复制指令/i });
    await userEvent.click(button);
  },
};

"use client";

import * as React from "react";
import { cn } from "@/lib/utils";

export interface CopyButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  value: string;
  successLabel?: string;
  fallbackLabel?: string;
}

async function copyWithFallback(text: string): Promise<boolean> {
  try {
    await navigator.clipboard.writeText(text);
    return true;
  } catch {
    try {
      const textarea = document.createElement("textarea");
      textarea.value = text;
      textarea.setAttribute("readonly", "");
      textarea.style.position = "absolute";
      textarea.style.left = "-9999px";
      document.body.appendChild(textarea);
      textarea.select();
      const result = document.execCommand("copy");
      document.body.removeChild(textarea);
      return result;
    } catch {
      return false;
    }
  }
}

export const CopyButton = React.forwardRef<HTMLButtonElement, CopyButtonProps>(
  ({ value, className, children, successLabel = "已复制", fallbackLabel = "复制失败", ...props }, ref) => {
    const [status, setStatus] = React.useState<"idle" | "success" | "error">("idle");

    const label = status === "success" ? successLabel : status === "error" ? fallbackLabel : children;

    return (
      <button
        ref={ref}
        type="button"
        className={cn(
          "inline-flex items-center gap-2 rounded-md border border-input bg-background px-3 py-2 text-sm font-medium shadow-sm transition-colors hover:bg-accent hover:text-accent-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2",
          className,
        )}
        aria-live="polite"
        onClick={async () => {
          const ok = await copyWithFallback(value);
          setStatus(ok ? "success" : "error");
          setTimeout(() => setStatus("idle"), 2000);
        }}
        {...props}
      >
        {label}
      </button>
    );
  },
);

CopyButton.displayName = "CopyButton";

import type { Meta, StoryObj } from "@storybook/react";

import { Spotlight, type SpotlightProps } from "./spotlight";

const meta: Meta<SpotlightProps> = {
  title: "Magic/Spotlight",
  component: Spotlight,
  argTypes: {
    accent: { control: "color" },
  },
};

export default meta;

type Story = StoryObj<SpotlightProps>;

export const Default: Story = {
  args: {
    children: (
      <div className="space-y-2">
        <p className="text-sm text-muted-foreground">Hover anywhere inside the card.</p>
        <p className="text-base font-semibold">Dynamic spotlight follows cursor.</p>
      </div>
    ),
  },
};

"use client";

import { cn } from "@/lib/utils";
import * as React from "react";

export interface SpotlightProps extends React.HTMLAttributes<HTMLDivElement> {
  accent?: string;
}

export function Spotlight({
  accent = "rgba(99, 102, 241, 0.15)",
  className,
  children,
  ...props
}: SpotlightProps): React.ReactElement {
  const [hovered, setHovered] = React.useState(false);

  return (
    <div
      {...props}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
      className={cn(
        "relative overflow-hidden rounded-xl border border-border bg-card p-6 transition-transform duration-300",
        hovered ? "shadow-lg ring-2 ring-primary/40 scale-[1.01]" : "shadow-sm",
        className,
      )}
      style={{
        backgroundImage: hovered
          ? `radial-gradient(600px circle at var(--x, 50%) var(--y, 50%), ${accent}, transparent 50%)`
          : undefined,
      }}
      onMouseMove={(event) => {
        const target = event.currentTarget.getBoundingClientRect();
        const x = ((event.clientX - target.left) / target.width) * 100;
        const y = ((event.clientY - target.top) / target.height) * 100;
        event.currentTarget.style.setProperty("--x", `${x}%`);
        event.currentTarget.style.setProperty("--y", `${y}%`);
      }}
    >
      {children ?? (
        <div className="space-y-2">
          <p className="text-sm font-medium text-muted-foreground">Magic Spotlight</p>
          <p className="text-base font-semibold text-foreground">Hover to reveal the glow</p>
        </div>
      )}
    </div>
  );
}
