  title: "Magic UI/Effects/AnimatedBeam",
  component: AnimatedBeam,
  parameters: {
    layout: "centered",
  },
  tags: ["autodocs"],
};

export default meta;
type Story = StoryObj<typeof AnimatedBeam>;

const BeamDemo = ({
